import React from 'react';
import {
  Route,
  Link,
  Switch
}
  from 'react-router-dom';
import Home from './Home';
import About from './About'
import Profile from './Profile';
// 경로 이동에서 <a></a>태그 를 사용하면 페이지가 완정히 다시 로드되기 때문에 사용해서는 안된다.

function App() {
  return (
    <div>
      <ul>
        <li>
          <Link to="/">홈</Link>
        </li>
        <li>
          <Link to="/about">소개</Link>
        </li>
        <li>
          <Link to="/profile">프로필 목록</Link>
        </li>
        <li>
          <Link to="/history">예제</Link>
        </li>
      </ul>
      <hr />
      <Switch>
        <Route exact path="/" component={Home} />{/**  "/"경로 가 지정되어있지 않은 페이지에서는 {Home}페이지로 이동하겠다. */}
        <Route path="/about" component={About} />
        <Route path="/profile/:username" component={Profile} /> {/** :username이 match.params의 username 이된다. */}
        < Route path="/history" component={HistorySample} />
        <Route render={({ location})=>(
          <div>
            <h2>이 페이지는 존재하지 않습니다.</h2>
            <p>{location.pathname}</p>
          </div>
        )}/>
      </Switch>

    </div>
  );
}

export default App;
