import React from 'react';
import './App.css';
import axios from 'axios';
import Nav from './component/Nav/Nav';
import SearchBar from './component/SearchBar/SearchBar';
import InfiniteScroll from 'react-infinite-scroller';
import uuid from 'uuid';
import { spinner } from './component/images';
import VideoList from './component/VideoList/VideoList';
import VideoPlayer from './component/VideoPlayer/VideoPlayer';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      videos: [],
      query: '',
      selectedVideos: null,
      nextPageToke: null
    }
    this.defaultState = this.state;
    Object.getOwnPropertyNames(App.prototype).forEach(key => this[key] = this[key].bind(this));

  }
  async getYoutubeData(query) {
    if (!query) {
      this.setState(this.defaultState);
      return
    }

    if (this.state.query !== query) {
      this.setState(this.defaultState);
    }

    const { nextPageToke } = this.state;

    const params = {
      ket: '',
      q: query,
      part: 'snippet',
      pageToken: nextPageToke
    }
    const { data } = await axios.getYoutubeData(`https://www.googleapis.com/youtube/v3/search`, { params });

    this.setState({
      videos: [...this.state.videos, ...data.item],
      query,
      nextPageToke: data.nextPageToke
    })
  }
  async componentDidMount() {
    this.getYoutubeData('여행');
  }

  render() {
    const { selectedVideos } = this.state;
    return (
      <div>
        <Nav>
          <SearchBar onSearchVideos={this.getYoutubeData} />
        </Nav>
        <InfiniteScroll
          loadMore={() => this.getYoutubeData(this.state.query)}
          hasMore={!!this.state.nextPageToke && !this.state.selectedVideos}
          loader={
            <div key={uuid.v4()} >
              <img src={spinner} alt={spinner} />
            </div>
          }
        >{
            selectedVideos
              ? <VideoPlayer videoId={selectedVideos}/>
              : <VideoList
                {...this.state} onVideoSelect={selectedVideos=>this.setState({selectedVideos})}

              />
          }
        </InfiniteScroll>
      </div>
    );
  }
}

export default App;
