import React from 'react';
import './App.css';
import axios from 'axios';
import Nav from './component/Nav/Nav';
import SearchBar from './component/SearchBar/SearchBar';
import InfiniteScroll from 'react-infinite-scroller';
import uuid from 'uuid';
import {spinner} from './component/images'
import VideoList from './component/VideoList/VideoList';

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      videos: [],
      query: '',
      selectedVideos: null,
      nextPageToke: null
    }
    Object.getOwnPropertyNames(Main.prototype).forEach(key => this[key] = this[key].bind(this));
    this.defaultState = this.state;
  }
  async getYoutubeData(query) {
    if (!query) {
      setTimeout(()=>{
        this.props.history.push(`/results?v=${query}`)
      },0)
      this.setState(this.defaultState);
      return
    }
    if (this.state.query !== query) {
      this.setState(this.defaultState);
      setTimeout(()=>{
        this.props.history.push(`/results?v=${query}`)
      },0);

    }
    const { nextPageToken } = this.state;
    const params = {
      key: "AIzaSyDFjSfrtwzoi9XHarGiQb7RB2x2UEayGIo",
      q: query,
      part: 'snippet',
      maxResults:10,
      pageToken: nextPageToken
    }
    const { data } = await axios.get(`https://www.googleapis.com/youtube/v3/search`, { params });
    this.setState({
      videos: [...this.state.videos, ...data.items],
      query,
      nextPageToke: data.nextPageToke
    })
  }
  async componentDidMount() {
    if(!this.props.location)
    this.getYoutubeData('여행');
  }
  render() {
    return (
      <div>
        <Nav>
          <SearchBar onSearchVideos={this.getYoutubeData} />
        </Nav>
        <InfiniteScroll
          // loadMore={() => this.getYoutubeData(this.state.query)}
          hasMore={!!this.state.nextPageToke && !this.state.selectedVideos}
          loader={
            <div key={uuid.v4()} >
              <img src={spinner} alt={spinner} />
            </div>
          }
          >
              <VideoList
                {...this.state} onVideoSelect={selectedVideos=>this.props.history.push(`/watch?v=${selectedVideos}`)}
              />
        </InfiniteScroll>
      </div>
    );
  }
}

export default Main;
