import React, { Component } from 'react';

class Controlled extends Component{
  state={
    value:''
  };

  render(){
    return (
      <div>
        <input type="text" onChange ={this.change} value={this.state.value}/>
      </div>
    )
  }
  change=(e)=>{
    console.log(e.target.value);
  }
}

export default Controlled;
