import React from 'react';
import Counter from '../component/Counter';
import { useSelector,useDispatch } from 'react-redux';//상태를 조회할때 useSelector을 사용한다.
import { increase, decrease, setDiff } from '../modules/counter';

function CounterContainer(){
  const { number,diff } =useSelector(state=>({//store.getState 하는 값들이 state의 상태로 들어간다.
    //useSelector의 결과물은 number와diff을 선택한 객체가 된다.
    number:state.counter.number,
    diff:state.counter.diff
  }));
  const dispatch=useDispatch();
  const onIncrease = () => dispatch(increase());
  const onDecrease = () => dispatch(decrease());
  const onSetDiff = diff =>dispatch(setDiff(diff));//diff을 파라미터로 받아와서 dispatch해준다. 

return <Counter />

}
export default CounterContainer;
