import React ,{useState} from 'react';

function TodoItem({todo,onToggle}){
  return (
<li
style={{
  textDecoration:todo.done?'line-through':'none'
}}
onClick={()=>onToggle(todo.id)}
>
  {todo.text}
</li>
  );
}
function TodoList({todos,onToggle}){//todos가 id,text,done등의 내용이 들어있는 객체(배열)
  return (
    <ul>
    {
      todos.map(todo=><TodoItem
      key={todo.id}
      todo={todo}
      onToggle={onToggle}
      />)
    }
    </ul>
  )
}
function Todos({todos,onCreate,onToggle}){
  const [text,setText]=useState('')//useState훅을 사용하여  나타냄
  const onChange=e=>setText(e.target.value);
  const onSubmit=e=>{
    e.preventDefault();
    onCreate(text);
    setText('');
  }

  return(
    <div>
      <form onSubmit={onSubmit}>
        <input
        value={text}
        onChange={onChange}
        placeholder="할일을 입력하세요..."/>
        <button type="submit">등록</button>
      </form>
      <TodoList
      todos={todos}
      onToggle={onToggle}

      />
    </div>
  )

}

export default Todos;
