import { createStore } from 'redux';
import { deflate } from 'zlib';

const INITIAL_STATE = {
  counter: 0,
  text: '',
  list: []
};
const INCRESE = 'INCRESE';
const DECREASE = 'DECREASE';
const CHANGE_TEXT = 'CHANGE_TEXT';
const ADD_TO_LIST = 'ADD_TO_LIST';

const increse = () => ({//액션 생성자 함수를 생성했다.
  type: INCRESE
});

const decrease = () => ({
  type: DECREASE
});

const changeText = text => ({
  type: CHANGE_TEXT,
  text
});

const addToList = item => ({
  type: ADD_TO_LIST,
  item
});

function reducer(state = INITIAL_STATE, action) {
  //state에 기본값을 설정한다.redux에서 초기상태를 만들때 reducer을 한번 호출한다.
  // 근데 그시점에 state가 undefined이면 deafult return undefined 되면서 초기상태가 만들어지지 않는다.
  // 그래서 에서 undefined일때 INITIAL_STATE을 출력하게끔 초기값을 설정해준다.
  switch (action.type) {
    case INCRESE:
      return {
        ...state,
        counter: state.counter + 1
      };
    case DECREASE:
      return {
        ...state,
        counter: state.counter - 1
      }
    case CHANGE_TEXT:
      return {
        ...state,
        text: action.text
      };
    case ADD_TO_LIST:
      return {
        ...state,
        list: state.list.concat(action.item)
      }

    default:
      return state;

  }
}
const store=createStore(reducer);//store가 만들어짐
console.log(store.getState());//store.getState()는 현재 store안에 있는 상태를 조회한다.
