import React from 'react';
//처음에 이런 방식을 사용하면 계속해서 배열이 늘어나거나 하는 경우에 사용할수없다.
//따라서 배열의 내장 함수인 map,forEach등을 사용한다.객체 상태의 배열 컴포넌트 형태의 배열로 변형을 해준다.

function User({user}){
  return(
    <div>
        <b>{user.username}</b><span>({user.email})</span>
      </div>
  )
}

function UserList() {
  const users = [
    {
      id: 1,
      username: 'kim',
      email: 'abx@naver.com'
    },
    {
      id: 2,
      username: 'park',
      email: 'zxc@gmail.com'
    },
    {
      id: 3,
      username: 'lee',
      email: 'zxczc@gmail.com'
    }
  ];
  return (
    <div>
      {
        users.map(
          user=>(<User user={user} />)
        )
      }

    </div>
  )
}
export default UserList;
