import React from 'react';

const SearchBar =props=>{
  let input;  
  return(
    <div>
      <input
      ref={ref=>(input=ref)}
      type="serach"
      placeholder="검색어를 입력하세요"
      onKeypress={e=>{
        if(e.key === 'Enter'){
          props.onSearchVideo(e.target.value);
        }
      }}
      onChange={()=>props.onSearchVideo(e.target.value)
      }
      />
      <button onClick={()=>props.onSearchVideo(input.value)}>

      </button>

    </div>

  )
}
